package com.zhoubi.graindepot.biz;import com.zhoubi.graindepot.bean.Authority;import com.zhoubi.graindepot.bean.BaseUser;import com.zhoubi.graindepot.mapper.AuthorityMapper;import com.zhoubi.graindepot.base.BaseMapper;import com.zhoubi.graindepot.base.BaseService;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.stereotype.Service;import java.util.List;import java.util.Map;@Servicepublic class AuthorityBiz extends BaseService<Authority>  {	@Autowired	private AuthorityMapper AuthorityMapper;	@Override	protected BaseMapper<Authority> getMapper() {		return AuthorityMapper;	}	public List<BaseUser> getUserList(Map param){		return AuthorityMapper.getUserList(param);	}	public boolean isAllow(Map param){		List<Authority> o= AuthorityMapper.isAllow(param);		if(o.size()==0){			return false;		}else {			return true;		}	}}