package com.zhoubi.graindepot.biz;import com.zhoubi.graindepot.bean.Company;import com.zhoubi.graindepot.mapper.CompanyMapper;import com.zhoubi.graindepot.base.BaseMapper;import com.zhoubi.graindepot.base.BaseService;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.stereotype.Service;import java.util.Map;@Servicepublic class CompanyBiz extends BaseService<Company>  {	@Autowired	private CompanyMapper CompanyMapper;	@Override	protected BaseMapper<Company> getMapper() {		return CompanyMapper;	}	public int checkRepeat(Map map) {		return CompanyMapper.checkRepeat(map);	}}