package com.zhoubi.graindepot.biz;import com.zhoubi.graindepot.bean.Group;import com.zhoubi.graindepot.mapper.GroupMapper;import com.zhoubi.graindepot.base.BaseMapper;import com.zhoubi.graindepot.base.BaseService;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.stereotype.Service;import java.util.Map;@Servicepublic class GroupBiz extends BaseService<Group>  {	@Autowired	private GroupMapper GroupMapper;	@Override	protected BaseMapper<Group> getMapper() {		return GroupMapper;	}	public int checkRepeat(Map map) {		return GroupMapper.checkRepeat(map);	}}