package com.zhoubi.graindepot.biz;import com.zhoubi.graindepot.base.BaseMapper;import com.zhoubi.graindepot.base.BaseService;import com.zhoubi.graindepot.bean.Option;import com.zhoubi.graindepot.mapper.OptionMapper;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.stereotype.Service;import java.util.List;import java.util.Map;@Servicepublic class OptionBiz extends BaseService<Option>  {	@Autowired	private OptionMapper OptionMapper;	@Override	protected BaseMapper<Option> getMapper() {		return OptionMapper;	}	public Option getDefaultOption(Map param){		return OptionMapper.getDefaultOption(param);	}	public List<Option> getOptions(Map map){		return OptionMapper.getOptions(map);	}}