package com.zhoubi.graindepot.biz;import com.zhoubi.graindepot.bean.Settle;import com.zhoubi.graindepot.mapper.SettleMapper;import com.zhoubi.graindepot.base.BaseMapper;import com.zhoubi.graindepot.base.BaseService;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.stereotype.Service;import java.util.Map;@Servicepublic class SettleBiz extends BaseService<Settle>  {	@Autowired	private SettleMapper SettleMapper;	@Override	protected BaseMapper<Settle> getMapper() {		return SettleMapper;	}	public int checkRepeat(Map map) {		return SettleMapper.checkRepeat(map);	}}